('568de2e31d88549', '', 'JSCSS bu', 'common.js and nubuilder.css bu', 'SELECT * FROM zzzsys_debug', 'zzzsys_debug', 'zzzsys_debug_id', 0, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', 'div {\n  padding: 0px;\n  margin: 0px\n}\n\nimg {\n  padding: 0px;\n  margin: 0px\n}\n\nul {\n  padding: 0px;\n  margin: 0px\n}\n\nli {\n  padding: 0px;\n  margin: 0px;\n  display: inline\n}\n\niframe {\n  padding: 0px;\n  margin: 0px;\n  border: 0px\n}\n\nselect {\n  padding: 0px;\n  margin: 0px;\n  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;\n}\n\ntextarea {\n  padding: 0px;\n  margin: 0px;\n  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;\n}\n\nbody {\n  padding: 0px;\n  margin: 0px;\n  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;\n  font-size: 13px;\n  line-height: 18px;\n  color: #202020;\n  background-color: #f4f4f4;\n}\n\ninput {border: 1px solid #e1e8ea;}\n\n\n.nuActionHolder {\n  background-color: white;\n  width: 99.9%;\n  height: 40px;\n  text-align: center;\n}\n\n.nuBreadcrumbHolder {\n  padding: 5px 0px 0px 0px;\n  background-color: white;\n  color: orange;\n  width: 99.9%;\n  font-size: 16px;\n}\n\n.nuBreadcrumb {\n  color: orange;\n  display: inline-block;\n  text-decoration: underline;\n}\n\n.nuBreadcrumb:hover {\n  cursor: pointer;\n}\n\n.nuNotBreadcrumb {\n  color: orange;\n  display: inline-block;\n}\n\n.nuFormHolder {\n  display: inline;\n  margin: 0px 5px;\n  position:relative;\n}\n\n.nuTabHolder {\n  background-color: white;\n  height: 25px;\n  width: 99.9%;\n  border-style: solid;\n  border-width: 0px 0px 1px 0px;\n  border-color: silver;\n  padding: 15px 0px 0px 2px;\n}\n\n.nuTab {\n  display: inline;\n  border-style: solid;\n  border-width: 0px;\n  border-color: silver;\n  font-size: 16px;\n  padding: 8px 10px 8px 10px;\n}\n\n.nuTabSelected {\n  background-color: #f4f4f4;\n  display: inline;\n  border-style: solid;\n  border-width: 1px;\n  border-color: silver silver white silver;\n  font-size: 16px;\n  padding: 8px 5px 8px 5px;\n  -webkit-border-radius: 5px 5px 0px 0px;\n  -moz-border-radius: 5px 5px 0px 0px;\n  border-radius: 5px 5px 0px 0px;\n}\n\n.nuBodyHolder {}\n\n.nuStatusHolder {\n  color: silver;\n  padding: 0px 0px 0px 0px;\n  background-color: white;\n  bottom: 10px;\n  width: 99.9%;\n  height: 20px;\n}\n\n.nuBrowseCell {}\n\n.nuButton {\n  color: white;\n  background-color: #00ADEF;\n  border: none;\n  height: 40px;\n  font-size: 13px;\n  display: inline;\n  border-style: solid;\n  border-width: 1px;\n  border-color: silver;\n  font-size: 16px;\n  padding: 8px 5px 8px 5px;\n  -webkit-border-radius: 5px 5px 5px 5px;\n  -moz-border-radius: 5px 5px 5px 5px;\n  border-radius: 5px 5px 5px 5px;\n}\n\n.nuLabel {}\n\n.nuText {}\n\n.nuLookup {border:1px solid #e1e8ea}\n\n.nuReadonly {background-color:lightgrey;border-style:none}\n\n.nuSearch {\n  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;\n  background-color: #abd886;\n  padding: 6px;\n  margin-bottom: 0;\n  font-size: 13px;\n  font-weight: normal;\n  line-height: 1.42857143;\n  text-align: left;\n  white-space: nowrap;\n  vertical-align: middle;\n  color: #666666;\n  border: 1px solid transparent;\n  height: 25px;\n}', NULL, NULL, '\n\nfunction testD(){\n\n	var w                       = new nuWindow();\n	\n	w.objects[w.objects.length] = {''top'':5,''left'':120,''width'':100,''id'':''test2'',''v'':''this much'', ''label'':''display test'',''type'':''display''};\n	w.objects[w.objects.length] = {''top'':35,''left'':120,''width'':100,''height'':200,''id'':''test1'',''html'':''abcd'', ''label'':''html test'',''type'':''html''};\n	w.objects[w.objects.length] = {''top'':65,''left'':120,''width'':100,''id'':''test3'',''v'':''just 1'', ''label'':''input test'',''type'':''input''};\n	w.objects[w.objects.length] = {''top'':95,''left'':120,''width'':200,''height'':77,''id'':''test4'',''v'':''just a test 2'', ''label'':''run test'',''type'':''run'', ''src'' : ''red.html'', ''run_method'' : ''buton''};\n	w.objects[w.objects.length] = {''top'':5,''left'':550,''width'':70,''id'':''test5'',''v'':''just a test 2'', ''label'':''another input'',''type'':''input''};\n	w.objects[w.objects.length] = {''top'':35,''left'':550,''width'':50,''id'':''test6'',''v'':''just a test 2'', ''label'':''a lookup'',''type'':''lookup'',''code'':''code'',''description'':''thedesc'', ''description_width'' : 150};\n	w.objects[w.objects.length] = {''top'':65,''left'':550,''width'':50,''id'':''test7'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select 1'',''type'':''select'', ''multiple'' : 0};\n	w.objects[w.objects.length] = {''top'':95,''left'':550,''width'':100,''id'':''test8'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select multiple'',''type'':''select'', ''multiple'' : 1};\n	w.objects[w.objects.length] = {''top'':305,''left'':150,''width'':500,''height'':200,''id'':''asf'',''v'':''hello again'', ''label'':''greeting'',''type'':''subform'', ''display_type'':''grid'', ''rows'': [,[3,''three'']]};\n\n	return w;\n}\n\n\n\nfunction row1(){\n\n	var r       = [];\n	var objects = [];\n	\n	objects[objects.length] = {''top'':5,''left'':200,''width'':70,''id'':''test5'',''v'':''just a test 2'', ''label'':''another input'',''type'':''input''};\n	objects[objects.length] = {''top'':35,''left'':200,''width'':50,''id'':''test6'',''v'':''just a test 2'', ''label'':''a lookup'',''type'':''lookup'',''c'':''code'',''d'':''thedesc'', ''description_width'' : 150};\n	objects[objects.length] = {''top'':65,''left'':200,''width'':50,''id'':''test7'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select 1'',''type'':''select'', ''multiple'' : 0};\n	objects[objects.length] = {''top'':95,''left'':200,''width'':100,''id'':''test8'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select multiple'',''type'':''select'', ''multiple'' : 1};\n\n	r[r.length] = objects;\n	\n	return r;\n	\n}\n\nfunction row2(){\n\n	var r       = [];\n	var objects = [];\n	\n	objects[objects.length] = {''top'':5,''left'':200,''width'':70,''id'':''test5'',''v'':''test line 2'', ''label'':''another input'',''type'':''input''};\n	objects[objects.length] = {''top'':35,''left'':200,''width'':50,''id'':''test6'',''v'':''another line 2'', ''label'':''a lookup'',''type'':''lookup'',''c'':''code'',''d'':''thedesc'', ''description_width'' : 150};\n	objects[objects.length] = {''top'':65,''left'':200,''width'':50,''id'':''test7'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select 1'',''type'':''select'', ''multiple'' : 0};\n	objects[objects.length] = {''top'':95,''left'':200,''width'':100,''id'':''test8'',''v'' : [[1,''one''],[3,''three'']], ''options'':[[1,''one''],[2,''two''],[3,''three'']], ''label'':''select multiple'',''type'':''select'', ''multiple'' : 1};\n\n	r[r.length] = objects;\n\n	return r;\n	\n}\n\n\n\n\nfunction nuBuildEditForm(w, p){\n\n	if(p == ''''){\n		\n		nuAddHolder(''nuStatusHolder'');\n		nuAddHolder(''nuActionHolder'');\n		nuAddHolder(''nuBreadcrumbHolder'');\n		nuAddHolder(''nuTabHolder'');\n		\n		$(''#nuStatusHolder'').append(w.user + '' :: Powered By nuBuilder'');\n		nuSetDefaultActions(w);\n		\n	}\n	\n	nuAddHolder(''nuFormsHolder'');\n	\n	$(''#nuFormsHolder'').html(''<div id="''+p+''EditForm" class="nuFormHolder"></div>'');\n	\n	nuBuildEditObjects(w, p);\n	\n}\n\nfunction nuSetDefaultActions(w){\n    \n\n	if(w.type == ''browse''){\n		\n		$(''#nuActionHolder'').append("<input id=''nuSearchField'' type=''text'' class=''nuSearch''>");\n		$(''#nuActionHolder'').append("<input id=''nuSearchButton'' type=''button'' class=''nuButton'' value=''Search''>");\n	}\n	\n	$(''#nuActionHolder'').append("<img id=''thelogo'' src=''logo.png'' style=''position:absolute;right:20px''>");\n	\n}\n\n\n\n\nfunction nuBuildEditObjects(w, p){\n	\n	var l = 0;\n\n	for(var i = 0 ; i < w.objects.length  ; i++){\n		\n		var t = w.objects[i].type;\n\n		if(t == ''input'' || t == ''display'' || t == ''lookup''){\n			\n			l = l + nuINPUT(w, i, l, p);\n			\n		}else if(t == ''run''){\n			\n			l = l + nuRUN(w, i, l, p);\n			\n		}else if(t == ''html''){\n			\n			l = l + nuHTML(w, i, l, p);\n			\n		}else if(t == ''select''){\n			\n			l = l + nuSELECT(w, i, l, p);\n			\n		}else if(t == ''subform''){\n			\n			l = l + nuSUBFORM(w, i, l, p);\n		}\n		l = l + 2;\n		\n	}\n	\n}\n\n\n\nfunction nuINPUT(w, i, l, p){\n\n	var id    = p + w.objects[i].id;\n	var ef    = p + ''EditForm'';                       //-- Edit Form Id\n	var inp   = document.createElement(''input'');\n\n	inp.setAttribute(''id'', id);\n	\n	if(w.objects[i].display_type == ''grid''){        //--  in a grid subform\n		\n		w.objects[i].left = l;\n		w.objects[i].top  = 0;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n\n	$(''#'' + ef).append(inp);\n	\n	$(''#'' + id).css({''top''       : Number(w.objects[i].top),\n					''left''       : Number(w.objects[i].left),\n					''width''      : Number(w.objects[i].width),\n					''width''      : Number(w.objects[i].width),\n					''text-align'' : w.objects[i].align,\n					''position''   : ''absolute''\n	}).val(w.objects[i].v);\n\n	if(w.objects[i].type == ''display''){\n		\n		$(''#'' + id).addClass(''nuReadonly'');\n		$(''#'' + id).prop(''readonly'', true);\n	\n	}\n\n	if(w.objects[i].type == ''lookup''){\n		\n		$(''#'' + id).hide();\n\n		id = p + w.objects[i].id + ''code'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n		\n    	$(''#'' + id).css({''top''              : Number(w.objects[i].top),\n		    			''left''              : Number(w.objects[i].left),\n			    		''width''             : Number(w.objects[i].width),\n						''background-color''  : ''lightblue'',\n						''position''          : ''absolute''\n		}).val(w.objects[i].code);\n\n		id = p + w.objects[i].id + ''button'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n		\n		$(''#'' + id).css({''top''      : Number(w.objects[i].top),\n						''left''      : Number(w.objects[i].left) + Number(w.objects[i].width),\n						''width''     : 30,\n						''position''  : ''absolute''\n		}).val(''b'').attr(''type'',''button'');\n\n		id = p + w.objects[i].id + ''description'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n\n		$(''#'' + id).css({''top''      : Number(w.objects[i].top),\n						''left''      : Number(w.objects[i].left) + Number(w.objects[i].width) + 30,\n						''width''     : w.objects[i].description_width,\n						''position''  : ''absolute''\n		}).val(w.objects[i].description);\n		\n		$(''#'' + id).addClass(''nuReadonly'');\n		$(''#'' + id).prop(''readonly'', true);\n		\n		return Number(w.objects[i].width) + Number(w.objects[i].description_width) + 30;\n		\n	}else{\n		\n		return Number(w.objects[i].width);\n		\n	}\n	\n}\n\n\n\n\nfunction nuHTML(w, i, l, p){\n\n	var id  = p + w.objects[i].id;\n	var ef  = p + ''EditForm'';                       //-- Edit Form Id\n	var inp = document.createElement(''div'');\n	\n	inp.setAttribute(''id'', id);\n	\n	if(w.objects[i].display_type == ''grid''){\n		\n		w.objects[i].left = l;\n		w.objects[i].top  = 0;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n	\n	$(''#'' + ef).append(inp);\n\n	$(''#'' + id).css({''top''     : Number(w.objects[i].top),\n					''left''     : Number(w.objects[i].left),\n					''width''    : Number(w.objects[i].width),\n					''height''   : Number(w.objects[i].height),\n					''position'' : ''absolute''\n	}).html(w.objects[i].html);\n	\n	return Number(w.objects[i].width);\n\n}\n\n\n\nfunction nuRUN(w, i, l, p){\n\n	var id  = p + w.objects[i].id;\n	var ef  = p + ''EditForm'';                       //-- 