n	return w + 5;\n	\n}	\n\n\nfunction nuGetSubformRowSize(o, t, id){\n\n    var l = 0;\n    var w = 0;\n    var h = 0;\n    var a = Array();\n//var t = ''e'';\n    for(var i = 0 ; i < o.length ; i++){\n\n        var H = Number(o[i].height) == 0        ? 25                             : Number(o[i].height);\n        var T = t                   == ''g''      ? 0                              : Number(o[i].top);\n        var B = o[i].type           == ''lookup'' ? 30                             : 0;    //-- lookup button\n        var D = o[i].type           == ''lookup'' ? Number(o[i].description_width) : 0;    //-- lookup description\n        \n        h = Math.max(h, H + T);\n        w = 2 + Number(o[i].width) + B + D;\n        \n        \n        if(t == ''g''){  //-- grid\n            \n            nuBuildSubformTitle(o[i], l, w, id);\n            l = l + w;\n            \n        }else{\n            \n            l = Math.max(l, w + Number(o[i].left));\n\n        }\n        \n    }\n    \n    a.push(l);\n    a.push(h + 5);\n    a.push(t == ''g'' ? 50 : 0);\n    \n    return a;\n    \n}\n\nfunction nuBuildSubformTitle(o, l, w, id){\n    \n	    var titleId  = id + o.id;\n    	var div = document.createElement(''div'');\n    	div.setAttribute(''id'', titleId);\n    	$(''#'' + id).append(div);\n    	\n    	$(''#'' + titleId).css({''top''     : 0,\n    					''left''          : Number(l),\n    					''width''         : Number(w),\n    					''height''        : 50,\n    					''text-align''    : ''center'',\n    					''border-width''  : 1,\n    					''border-style''  : ''solid'',\n    					''border-color''  : ''green'',\n    					''position''      : ''absolute''\n    	}).html(o.label);\n        \n    \n}\n\n\n', NULL, 0, 0, '2016-01-11 13:43:55', 'globeadmin', '2016-01-11 13:43:55', 'globeadmin', '2016-02-10 13:43:07', 'globeadmin');
