nd("<input id=''nuSearchField'' type=''text'' class=''nuSearch''>");\n		$(''#nuActionHolder'').append("<input id=''nuSearchButton'' type=''button'' class=''nuButton'' value=''Search''>");\n	}\n	\n	$(''#nuActionHolder'').append("<img id=''thelogo'' src=''logo.png'' style=''position:absolute;right:20px''>");\n	\n}\n\n\n\n\nfunction nuBuildEditObjects(w, p, o){\n	\n	var l = 0;\n\n	for(var i = 0 ; i < w.objects.length  ; i++){\n\n		var t                       = w.objects[i].Y;\n		w.objects[i].parent_type    = o.subform_type;\n\n		if(t == ''input'' || t == ''display'' || t == ''lookup''){\n			\n			l = l + nuINPUT(w, i, l, p);\n			\n		}else if(t == ''run''){\n			\n			l = l + nuRUN(w, i, l, p);\n			\n		}else if(t == ''html''){\n			\n			l = l + nuHTML(w, i, l, p);\n			\n		}else if(t == ''select''){\n			\n			l = l + nuSELECT(w, i, l, p);\n			\n		}else if(t == ''subform''){\n\n			l = l + nuSUBFORM(w, i, l, p);\n		}\n		\n		l = l + 2;\n		\n	}\n	\n}\n\n\n\nfunction nuINPUT(w, i, l, p){\n\n	var id    = p + w.objects[i].I;\n	var ef    = p + ''EditForm'';                       //-- Edit Form Id\n	var inp   = document.createElement(''input'');\n\n	inp.setAttribute(''id'', id);\n	\n	if(w.objects[i].parent_type == ''g''){        //--  in a grid subform\n		\n		w.objects[i].L = l;\n		w.objects[i].T = 3;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n\n	$(''#'' + ef).append(inp);\n	nuAddDataTab(id, w.objects[i].B, p);\n	$(''#'' + id).css({''top''       : Number(w.objects[i].T),\n					''left''       : Number(w.objects[i].L),\n					''width''      : Number(w.objects[i].W),\n					''width''      : Number(w.objects[i].W),\n					''text-align'' : w.objects[i].align,\n					''position''   : ''absolute''\n	}).val(w.objects[i].V);\n\n	if(w.objects[i].Y == ''display''){\n		\n		$(''#'' + id).addClass(''nuReadonly'');\n		$(''#'' + id).prop(''readonly'', true);\n	\n	}\n\n	if(w.objects[i].Y == ''lookup''){\n		\n		$(''#'' + id).hide();\n\n		id = p + w.objects[i].I + ''code'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n    	nuAddDataTab(id, w.objects[i].B, p);\n    	$(''#'' + id).css({''top''              : Number(w.objects[i].T),\n		    			''left''              : Number(w.objects[i].L),\n			    		''width''             : Number(w.objects[i].W),\n						''background-color''  : ''lightblue'',\n						''position''          : ''absolute''\n		}).val(w.objects[i].code);\n\n		id = p + w.objects[i].I + ''button'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n    	nuAddDataTab(id, w.objects[i].B, p);\n		$(''#'' + id).css({''top''      : Number(w.objects[i].T),\n						''left''      : Number(w.objects[i].L) + Number(w.objects[i].W),\n						''width''     : 30,\n						''position''  : ''absolute''\n		}).val(''b'').attr(''type'',''button'');\n\n		id = p + w.objects[i].I + ''description'';\n		var inp = document.createElement(''input'');\n		inp.setAttribute(''id'', id);\n		\n		$(''#'' + ef).append(inp);\n    	nuAddDataTab(id, w.objects[i].B, p);\n		$(''#'' + id).css({''top''      : Number(w.objects[i].T),\n						''left''      : Number(w.objects[i].L) + Number(w.objects[i].W) + 30,\n						''width''     : w.objects[i].description_width,\n						''position''  : ''absolute''\n		}).val(w.objects[i].description);\n		\n		$(''#'' + id).addClass(''nuReadonly'');\n		$(''#'' + id).prop(''readonly'', true);\n		\n		return Number(w.objects[i].W) + Number(w.objects[i].description_width) + 30;\n		\n	}else{\n		\n		return Number(w.objects[i].W);\n		\n	}\n	\n}\n\n\n\n\nfunction nuHTML(w, i, l, p){\n\n	var id  = p + w.objects[i].I;\n	var ef  = p + ''EditForm'';                       //-- Edit Form Id\n	var inp = document.createElement(''div'');\n	\n	inp.setAttribute(''id'', id);\n	\n	if(w.objects[i].parent_type == ''g''){\n		\n		w.objects[i].L = l;\n		w.objects[i].T = 3;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n	\n	$(''#'' + ef).append(inp);\n	nuAddDataTab(id, w.objects[i].B, p);\n	$(''#'' + id).css({''top''     : Number(w.objects[i].T),\n					''left''     : Number(w.objects[i].L),\n					''width''    : Number(w.objects[i].W),\n					''height''   : Number(w.objects[i].H),\n					''position'' : ''absolute''\n	}).html(w.objects[i].html);\n	\n	return Number(w.objects[i].W);\n\n}\n\n\n\nfunction nuRUN(w, i, l, p){\n\n	var id  = p + w.objects[i].I;\n	var ef  = p + ''EditForm'';                       //-- Edit Form Id\n	var ele = ''input'';\n	\n	if(w.objects[i].parent_type == ''g''){\n		\n		w.objects[i].L = l;\n		w.objects[i].T = 3;\n		\n	}\n	\n	if(w.objects[i].run_method != ''b''){\n		\n		ele = ''iframe'';\n		\n	}\n\n	if(!w.objects[i].parent_type == ''g'' && w.objects[i].run_method != ''b''){\n		\n		nuLabel(w, i, p);\n		\n	}\n\n	var inp = document.createElement(ele);\n	\n	inp.setAttribute(''id'', id);\n	\n	$(''#'' + ef).append(inp);\n	nuAddDataTab(id, w.objects[i].B, p);\n	$(''#'' + id).css({''top''     : Number(w.objects[i].T),\n					''left''     : Number(w.objects[i].L),\n					''width''    : Number(w.objects[i].W),\n					''height''   : Number(w.objects[i].H),\n					''position'' : ''absolute''\n	});\n\n	if(w.objects[i].run_method == ''b''){\n	\n    	$(''#'' + id).attr({''type'':''button'',''value'':w.objects[i].L})\n		\n	}else{\n	    \n    	$(''#'' + id).attr({''src'':w.objects[i].src})\n    	$(''#'' + id).attr({''src'':''https://nubuilder.net''})\n\n\n	}\n	\n	return Number(w.objects[i].W);\n	\n}\n\n\n\n\nfunction nuSELECT(w, i, l, p){\n\n	var id  = p + w.objects[i].I;\n	var ef  = p + ''EditForm'';                       //-- Edit Form Id\n	\n	if(w.objects[i].parent_type == ''g''){\n		\n		w.objects[i].L = l;\n		w.objects[i].T = 3;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n\n	var sel = document.createElement(''select'');\n	\n	sel.setAttribute(''id'', id);\n	nuAddDataTab(id, w.objects[i].B, p);\n	$(''#'' + ef).append(sel);\n	$(''#'' + id).attr(''data-nuTab'', w.objects[i].B);\n	$(''#'' + id).css({''top''     : Number(w.objects[i].T),\n					''left''     : Number(w.objects[i].L),\n					''width''    : Number(w.objects[i].W),\n					''position'' : ''absolute''\n	});\n\n\n\n	if(w.objects[i].multiple == 1){\n	    \n		$(''#'' + id).attr(''multiple'', ''multiple'');\n    	$(''#'' + id).css(''height'', Number(w.objects[i].H));\n    	\n	}\n	\n	var values = String(w.objects[i].V).split(''#nuSep#'');\n\n	for(var n = 0 ; n < w.objects[i].options.length ; n++){\n\n		if(values.indexOf(w.objects[i].options[n][0]) == -1){\n		    $(''#'' + id).append(''<option  value="''+w.objects[i].options[n][0]+''">''+w.objects[i].options[n][1]+''</option>'');\n		}else{\n		    $(''#'' + id).append(''<option selected="selected "value="''+w.objects[i].options[n][0]+''">''+w.objects[i].options[n][1]+''</option>'');\n		}\n		\n	}\n\n	return Number(w.objects[i].W);\n	\n}\n\n\n\nfunction nuSUBFORM(w, i, l, p){\n\n    var SF  = w.objects[i];\n	var id  = p + SF.I;\n	var ef  = p + ''EditForm'';                       //-- Edit Form Id\n	var inp = document.createElement(''div'');\n	var fms = SF.forms;\n	\n	inp.setAttribute(''id'', id);\n	\n	if(SF.parent_type == ''g''){\n		\n		SF.left = l;\n		SF.top = 3;\n		\n	}else{\n		\n		nuLabel(w, i, p);\n		\n	}\n	\n	$(''#'' + ef).append(inp);\n	nuAddDataTab(id, w.objects[i].B, p);\n	$(''#'' + id).css({''top''          : Number(SF.T),\n					''left''          : Number(SF.L),\n					''width''         : Number(SF.W),\n					''height''        : Number(SF.H),\n					''border-width''  : Number(1),\n					''border-style''  : ''solid'',\n					''overflow-x''    : ''hidden'',\n					''overflow-y''    : ''hidden'',\n					''position''      : ''absolute''\n	});\n\n	var rowSize     = nuGetSubformRowSize(SF.forms[0].objects, SF, id);\n	var rowWidth    = rowSize[0]\n	var rowHeight   = rowSize[1];\n	var rowTop      = rowSize[2];\n	\n    var scrId  = id + ''scrollDiv'';\n	var scrDiv = document.createElement(''div'');\n	scrDiv.setAttribute(''id'', scrId);\n	$(''#'' + id).append(scrDiv);\n	$(''#'' + scrId).css({''top'