('nuhome', 'launch', 'nuhome', 'Home', '', '', '', 0, 0, '', '\n\nnuAttachImage(''#run_php'',''RP'');\nnuAttachImage(''#run_setup'',''SET'');\nnuAttachImage(''#form_button'',''F'');\nnuAttachImage(''#run_lang'',''TR'');\nnuAttachImage(''#run_report'',''RR'');\nnuAttachImage(''#user_home'',''UH'');\nnuAttachImage(''#run_user'',''U'');\nnuAttachImage(''#run_file'',''FILE'');\nnuAttachImage(''#edit_php'',''P'');\nnuAttachImage(''#edit_report'',''R'');\nnuAttachImage(''#run_access'',''KEY'');\nnuAttachImage(''#run_fast_form'',''FF'');\nnuAttachImage(''#run_format'',''FMT'');\nnuAttachImage(''#run_fast_report'',''FR'');\nnuAttachImage(''#object_button'',''OB'');\nnuAttachImage(''#run_sql'',''SQL'');\nnuAttachImage(''#open_database'',''DB'');\nnuAttachImage(''#system_update'',''SU'');\n\n\n$(''.nuActionButton'').hide();\n\n$(''#user_home'')\n.css({\n    ''color''             : ''white'',\n    ''font-size''         : 13,\n    ''display''           : ''inline'',\n    ''border-style''      : ''solid'',\n    ''height''            : 30,\n    ''font-size''         : 16,\n    ''text-shadow''       : ''0 1px 2px #9AB973'',\n    ''border-color''      : ''#9AB973'',\n    ''border-width''      : ''0px 0px 1px 0px'',\n    ''background-color''  : ''#88cb51''\n});\n\n$(''#open_database'')\n.attr(''title'', ''PHPMyAdmin'')\n//.click(function(){\n//    window.open(''nupmalogin.php'');\n//});\n\n\n$(''#run_php'')\n.css(''text-align'', ''left'')\n.css(''padding'', ''0px 0px 0px 35px'')\n.css(''background-size'', ''30px'')\n.css(''background-repeat'', ''no-repeat'');\n\nnuGetStartingTab();\n'),
('nubrowse', 'subform', 'nubrowse', 'Browse Columns', 'zzzzsys_browse', 'zzzzsys_browse_id', '', 0, 5, 'SELECT * FROM zzzzsys_browse\nORDER BY sbr_order\n', '$(''#nuSearchButton'').remove();\n$(''#nuSearchField'').remove();\n$(''#nuPrintButton'').remove();\n\nnuSetTitle($(''#sbr_title'').val());\n\nvar pid = parent.nuFORM.getCurrent().record_id;\n\nif(nuFORM.getCurrent().record_id == -1){\n    $(''#sbr_zzzzsys_form_id'').val(pid).change();\n}\n'),
('nutab', 'browseedit', 'nutab', 'Form Tab', 'zzzzsys_tab', 'zzzzsys_tab_id', '', 0, 0, 'SELECT * FROM zzzzsys_tab\nJOIN zzzzsys_form ON zzzzsys_form_id = syt_zzzzsys_form_id\nORDER BY syt_order', '\n$("[data-nu-column=''nucolumn003'']").each(function() {\n    $(this).addClass(''nu_''+this.innerHTML);\n});\n'),
